        discountPrice: {
            type: Number
        },
        bookImage: {
            type: String
        },
        bookName: {
            type: String
        },
        author: {
            type: String,
        },
        quantity: {
            type: Number
        },
        price: {
            type: Number
        }
    },
    {
        timestamps: true
    }
)
export default model('Book', bookSchema);

