import Whishlist from '../models/whishlist.model';
import Book from '../models/book.model';

export const addWishlist = async (params, body) => {
    const searchBook = await Book.findOne({ _id: params._id });
    if (searchBook == null) {
        throw new Error('Book does not exist')
    }
    else {
        const listPresent = await Whishlist.findOne({ userId: body.userId })
        if (listPresent) {
            let newBook = {
                bookId: searchBook._id,
                bookName: searchBook.bookName,
                description: searchBook.description,
                author: searchBook.author,
                price: searchBook.price,
            }
            listPresent.book.push(newBook);
            const listUpdated = await Whishlist.findByIdAndUpdate({_id: listPresent._id},{
                $set:{
                  book: listPresent.book
                }
            },
            {
                new: true
            });
            return listUpdated
           
        } else {
            let newlist = Whishlist.create({
                'userId': body.userId,
                'book': {
                    _id: searchBook._id,
                    bookName: searchBook.bookName,
                    description: searchBook.description,
                    author: searchBook.author,
                    price: searchBook.price,
                }
            })
            return newlist;
        }
    }
};

